public function getStatusName($order_status_id) {
    $query = $this->db->query(
        "SELECT name
        FROM order_status
        WHERE order_status_id = '" . (int)$order_status_id . "'
        AND language_id = '" . (int)$this->config->get('config_language_id') . "'
        LIMIT 1"
    );
    return $query->row['name'];
}


